CREATE TABLE `tasks` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `task_type` ENUM('DROPBOX_SYNC','DROPBOX_FILE_IMPORT','PROCESS_IMAGE_FILE','PROCESS_VIDEO_FILE','REMOVE_PROCESSING_FILE','ADDRESS_LOOKUP','ELEVATION_LOOKUP','PLANT_LOOKUP','OCR_GENERIC','OCR_NUMBERPLATE','SUBJECT_DETECTION') NOT NULL , `related_picili_file_id` INT UNSIGNED NOT NULL , `from` DATETIME NOT NULL , `after` INT UNSIGNED NULL DEFAULT NULL , `import_task` BOOLEAN NOT NULL DEFAULT FALSE , `priority` TINYINT UNSIGNED NOT NULL , `times_seen` SMALLINT UNSIGNED NOT NULL DEFAULT '0' , `created_at` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, PRIMARY KEY (`id`)) ENGINE = InnoDB; 