CREATE TABLE `files` ( `id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `user_id` INT UNSIGNED NOT NULL , `dropbox_file_id` INT UNSIGNED NOT NULL , `uuid` VARCHAR(36) NOT NULL , `is_thumbnailed` BOOLEAN NOT NULL DEFAULT FALSE , `is_corrupt` BOOLEAN NOT NULL DEFAULT FALSE , `latitude` DECIMAL(8,6) NULL DEFAULT NULL , `longitude` DECIMAL(9,6) NULL DEFAULT NULL , `elevation` DECIMAL(8,4) NULL DEFAULT NULL , `address` VARCHAR(255) NULL DEFAULT NULL , `file_directory` VARCHAR(255) NOT NULL , `file_name` VARCHAR(64) NOT NULL , `file_extension` VARCHAR(8) NOT NULL , `datetime` DATETIME NULL DEFAULT NULL , `medium_height` SMALLINT UNSIGNED NULL DEFAULT NULL , `medium_width` SMALLINT UNSIGNED NULL DEFAULT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB; 